import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { Observable, mergeMap, map } from 'rxjs';
import { City } from 'src/models/city';
import { GetAccomodationsService } from 'src/app/services/get-accomodations.service';
import { NgIf } from '@angular/common';



@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {

  villes$: Observable<Array<City>>
  userInput: string = ''
  tabCity: string[] = [];
  villes: string[] = ['Paris', 'Marseille', 'Lyon', 'Toulouse', 'Nice'];


  @Output() search: EventEmitter<string> = new EventEmitter()


  constructor(private service: GetAccomodationsService){}

  ngOnInit(): void{
      this.villes$ = this.service.getCitiesByName('Nan')

  }



  public onInputChange(search: any): void {
    this.search.emit(search.target.value)
    this.villes$ = this.service.getCitiesByName(search.target.value)
  }

}

<html>

<div class="logo">
  <a href="http://localhost:4200/">
    <img src="../../../assets/logo.png" height="25" />
  </a>
</div>




  <div class="search-bar">

      <input list="city-suggestions" (input)="onInputChange($event)" type="text" id="where" placeholder="N'importe où">
      <h5 id="|" >|</h5>
      <h5 id="txt" >Une semaine</h5>
      <h5 id="txt" >Ajouter des voyageurs</h5>
      <img id="fleche" src="../../../assets/fleche.svg" height="25">

  </div>


    <datalist id="city-suggestions">
      <option
        *ngFor="let ville of villes$ | async"
        [value]="ville.nom"
      ></option>
    </datalist> 





  <h5 class="text">Mettre mon logement sur Airbnb</h5>

  <div class="img-1">
    <img src="../../../assets/world.svg"> 
  </div>

  <div class="img-2">
    <img src="../../../assets/menu.svg">
    <img src="../../../assets/account.svg">
  </div>

</html>
