import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Logementms } from 'src/app/models/NavItems';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  items: Array<string> = ['Product', 'Solution', 'Open Source', 'Pricing']

  navItems$ : Observable<Array<NavItems>>;
  showSub: boolean;
  selectedItem: NavItems;

  constructor(private http:HttpClient){}

  ngOnInit() : void {

    // this.navItems$ = this.http.get('assets/data/nav-items.json') as Observable<
    // Array<NavItems>
    // >;


    const url = 'http://localhost:3000/accomodations';
    this.navItems$ = this.http.get<Array<NavItems>>(url);
  }

  public itemHover(item : NavItems):void{
    this.showSub = true;
    this.selectedItem = item;
  }

}
