import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { Observable } from 'rxjs';
import { City } from 'src/models/city';
import { GetAccomodationsService } from 'src/app/services/get-accomodations.service';
import { NgIf } from '@angular/common';
import { DataHeadertoCardService } from 'src/app/services/data-headerto-card.service';



@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {

  villes$: Observable<Array<City>>
  userInput : String = 'test';

  @Output() search: EventEmitter<string> = new EventEmitter()


  constructor(private service: GetAccomodationsService, private dataShare : DataHeadertoCardService){}

  ngOnInit(): void{
      this.villes$ = this.service.getCitiesByName('Nante')
  }

  setData() {
    this.dataShare.setSharedData('userInput')
  }



}

<html>

  <a href="http://localhost:4200/">
    <img class="logo" src="../../../assets/logo.png" height="25" />
  </a>




  <div class="search-bar">
    <form action="/rechercher" method="get">
      <input (input)="onInputChange($event)" type="text" id="where" placeholder="N'importe où" class="search-bar p1" required>
      <input type="text" id="when" placeholder="Une semaine" class="search-bar p1" required>
      <input type="text" id="who" placeholder="Ajoutez des voyageurs" class="search-bar p2" required>
    </form>







  </div>

  <img class="fleche" src="../../../assets/fleche.svg" height="25">

  <h5 class="text">Mettre mon logement sur Airbnb</h5>

  <div class="img-1">
    <img src="../../../assets/world.svg"> 
  </div>

  <div class="img-2">
    <img src="../../../assets/menu.svg">
    <img src="../../../assets/account.svg">
  </div>

</html>
