import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { GetAccomodationsService } from 'src/app/services/get-accomodations.service';
import { Logement } from 'src/models/Logements';

@Component({
  selector: 'app-page-details',
  templateUrl: './page-details.component.html',
  styleUrls: ['./page-details.component.scss']
})
export class PageDetailsComponent {
  id_recue : number
  accomodation$ : Observable <Array<Logement>>

  constructor(private route : ActivatedRoute, private service: GetAccomodationsService){
    this.route.queryParams.subscribe(params=> {
      this.id_recue = params['id'];
      console.log('Variable reçue :', this.id_recue);
    })

    this.accomodation$ = this.service.getAccomodations()
  }
}






<app-header></app-header>

<p id="test">{{id_recue}}</p>

<div class="cards" *ngFor="let accomodation of accomodationsFiltered" >

    <div class="fav">
        <img (click)="onClick(accomodation.id)"  class="maison" [src]="'data:image/jpeg;base64,' +  accomodation.image">
        <img class="like" src="../../../assets/heart.svg"  [ngClass]="{'filter-fav': accomodation.favourite}">
    </div>


    <div class="name">
        <h3>{{ accomodation.city.name}}, {{ accomodation.city.zipCode}}, France</h3>
            <div class="rating">
                <img class="star" src="../../../assets/star.svg">
                <h3>{{ accomodation.rating}}</h3>
            </div>
    </div>


    <h4>Particulier</h4>

    <h4>{{ accomodation.price}}€ par nuit</h4>


</div>
