import { Component, Input, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { DataHeadertoCardService } from 'src/app/services/data-headerto-card.service';
import { GetAccomodationsService } from 'src/app/services/get-accomodations.service';
import { Logement } from 'src/models/Logements';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.scss']
})

export class CardComponent implements OnInit {

  cityInput : String


  accomodation$: Observable <Array<Logement>>

  constructor (private service: GetAccomodationsService, private dataShare : DataHeadertoCardService){
    this.cityInput = this.dataShare.getSharedData()
  }

  ngOnInit(): void {
    this.accomodation$ = this.service.getAccomodations()
  }


}
